import React, {Component} from "react";
import {FeatureGroup, GeoJSON, MapContainer,TileLayer} from 'react-leaflet' 
import 'leaflet/dist/leaflet.css' 

class MapGeoJson extends Component {
    constructor(props){
        super(props)
        this.state = { 
                    data:[],
                    center:[13.2955977,102.2090103],
                    zoom:6,
                    url:'http://127.0.0.1:5000/get_tambon/',
                    province:'Phichit'
                    } 
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        this.fetchData(this.state.url);
        console.log('did mount');
    }
    fetchData(url) {
        let request = fetch(url);
    
        request
          .then(r => r.json())
          .then(data => {
            this.setState({
              data: data.features
            });
          }, (error) => {
            console.error(error);
          });
    }
    myStyle = () => {
        return {
          color: "green",
          weight: 3,
          opacity: 1,
          fillColor: "red",
          dashArray: '8 5'
        }
      }

      handleChange(event) {
        this.setState({url: event.target.value});
      }
    
      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
      }
    render() { 
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>
                    Name:
                    <input type="text" value={this.state.value} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
                <MapContainer center={this.state.center} zoom={this.state.zoom} scrollWheelZoom={true} style={{height:'90vh'}}>
                    <TileLayer
                        url='https://api.maptiler.com/maps/hybrid/{z}/{x}/{y}.jpg?key=UWwVBYXDjNUkBGiF7hvU'
                        attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
                        maxZoom= {20}
                    />
                    <FeatureGroup>
                        <GeoJSON key={this.state.data.properties}  data={this.state.data} style={this.myStyle}>
                            {console.log(this.state.data)}
                        </GeoJSON>
                    </FeatureGroup>
                </MapContainer>
            </div>
        
        
        );
    }
}
 
export default MapGeoJson;
