import React, {Component} from "react";
import {FeatureGroup, GeoJSON, MapContainer,TileLayer} from 'react-leaflet' 
import 'leaflet/dist/leaflet.css' 
import {Modal, Input} from 'antd'

class MapGeoJson extends Component {
        state = { 
                    data:[],
                    center:[13.2955977,102.2090103],
                    zoom:6,
                    url:'http://127.0.0.1:5000/get_province/',
                    province:'Phichit'
                    } 
  
    componentDidMount() {
        this.fetchData(this.state.url + this.state.province);
        console.log('did mount');
    }
    fetchData(url) {
        let request = fetch(url);
    
        request
          .then(r => r.json())
          .then(data => {
            this.setState({
              data: data.features
            });
          }, (error) => {
            console.error(error);
          });
    }
    input_styles = () =>{
      return({
      height: 40,
      margin: 12,
      borderWidth: 1,
      padding: 10,
      })
    } 
      
    myStyle = () => {
        return {
          color: "green",
          weight: 1,
          opacity: 1,
          fillColor: "red",
          dashArray: '8 5'
        }
      }

    handleOk = (e) => {
      console.log(this.state.province+'Form Button OK');
      this.fetchData(this.state.url + this.state.province);
    };
  
    handleCancel = e => {
      
    };
  
    onLatChange = (e) => {
      this.setState({
        province: e.target.value,
      })
    }

    render() { 
        return (
          <div>

              <Modal
                visible={true}
                onOk={this.handleOk}
                onCancel={this.handleCancel}
              >
                <Input type="text" addonBefore="Province : " defaultValue={this.state.province}
                  onChange = {this.onLatChange} />
                <button></button>
                <br /><br />
              </Modal>

              <MapContainer center={this.state.center} zoom={this.state.zoom} scrollWheelZoom={true} style={{height:'80vh'}}>
                  <TileLayer
                      url='https://api.maptiler.com/maps/hybrid/{z}/{x}/{y}.jpg?key=UWwVBYXDjNUkBGiF7hvU'
                      attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
                      maxZoom= {20}
                  />
                  <FeatureGroup>
                    <GeoJSON key={this.state.data.properties}  data={this.state.data} style={this.myStyle}>
                        {console.log(this.state.data)}
                        {console.log(this.state.province)}
                    </GeoJSON>
                  </FeatureGroup>
              </MapContainer>
            </div>
        );
    }
}
 
export default MapGeoJson;
