import React, {Component} from "react";
import {FeatureGroup, GeoJSON, MapContainer} from 'react-leaflet' 

class MapGeoJson extends Component {
    state = { 
        data:[]
     } 

    componentDidMount() {
        this.fetchData('http://127.0.0.1:5000/get_tambon/หนองจะบก');
        console.log('did mount');
    }
    fetchData(url) {
        let request = fetch(url);
    
        request
          .then(r => r.json())
          .then(data => {
            this.setState({
              data: data.features
            });
          }, (error) => {
            console.error(error);
          });
    }
    render() { 
        console.info(this.state.data);
        return (
        <MapContainer center={[14.8986333,102.0650107]} zoom={14} scrollWheelZoom={true} style={{height:'85vh'}}>
            <FeatureGroup>
                {this.state.data.map(f => {
                return <GeoJSON key={f.properties.id} data={f} style={this.myStyle}>
                </GeoJSON>
                })}
            </FeatureGroup>
        </MapContainer>
        
        );
    }
}
 
export default MapGeoJson;
