import React, {useEffect, useState} from 'react'
import { MapContainer, TileLayer, GeoJSON,FeatureGroup} from "react-leaflet";
import 'leaflet/dist/leaflet.css'
import { mapData } from '../data/TH_Province';
import {data} from '../data/thailand.js'

const MyMap = () => {
    
    // use for lifecycle react hook
    const [data, setData] = useState({})
    const [error, setError] = useState(false)

    // fech API 
    async function fetchData(){
      const res = await fetch('http://127.0.0.1:5000/get_tambon/หนองจะบก')
      res.json().then(res => setData(res.features)).catch(err => setError(err))
    }

    useEffect(() => {
      fetchData()
    })
    const myStyle = () => {
      return {
        color: "green",
        weight: 3,
        opacity: 1,
        fillColor: "red",
        dashArray: '8 5'
      }
    }

    return (
    <div>
        <h1 style={{textAlign:'center'}}>My Map React Leaftlet</h1>
        {/* 14.8986333,102.0650107    51.505, -0.09 thailand: 13.1235091,95.7603955*/}
        <MapContainer center={[13.1235091,95.7603955]} zoom={5} scrollWheelZoom={true} style={{height:'85vh'}}>
            <TileLayer
                url='https://api.maptiler.com/maps/hybrid/{z}/{x}/{y}.jpg?key=UWwVBYXDjNUkBGiF7hvU'
                attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
                maxZoom= {20}
            />
            <FeatureGroup>
              {this.state.data.map(f => {
                return <GeoJSON key={f.properties.P_NAME_T} data={f} style={myStyle}>
                  
                </GeoJSON>
              })}
            </FeatureGroup>
            {/* {
              mapData.features.map((p) => {
                const coordinates = p.geometry.coordinates[0].map((item)=>[[item[1], item[0]]])
                console.log(coordinates);
                return (<Polygon
                  pathOptions={{
                    fillColor:'#FD8D3C',
                    fillOpacity: 0.7,
                    weight:2,
                    opacity: 1,
                    dashArray:3,
                    color:'white'
                  }}
                  positions={coordinates}
                />)
              })
            } */}
            {/* <GeoJSON data={mapData.features}/> */}
            
        </MapContainer>
        {/* <div>{JSON.stringify(data.features)}</div> */}
        <div>{JSON.stringify(mapData)}</div>
    </div>)
    
}

export default MyMap
