import React from 'react'
import { useState } from 'react';
import {GeoJSON, MapContainer, TileLayer, Popup, FeatureGroup,LayersControl} from 'react-leaflet' 
import Legend from "./Legend";
import './selectProvince';

function MainMap() {

    const [data, setData] = useState([])
    const [center, setcenter] = useState([13.2955977,102.2090103])
    const [zoom, setzoom] = useState(6)
    const [url, seturl] = useState('http://127.0.0.1:5000/get_grid')
    const [all_P, setall_P] = useState(['Amnat Charoen', 'Ang Thong', 'Bangkok Metropolis', 'Bueng Kan', 'Buri Ram', 'Chachoengsao', 'Chai Nat', 'Chaiyaphum', 'Chanthaburi', 'Chiang Mai', 'Chiang Rai', 'Chon Buri', 'Chumphon', 'Kalasin', 'Kamphaeng Phet', 'Kanchanaburi', 'Khon Kaen', 'Krabi', 'Lampang', 'Lamphun', 'Loei', 'Lop Buri', 'Mae Hong Son', 'Maha Sarakham', 'Mukdahan', 'Nakhon Nayok', 'Nakhon Pathom', 'Nakhon Phanom', 'Nakhon Ratchasima', 'Nakhon Sawan', 'Nakhon Si Thammarat', 'Nan', 'Narathiwat', 'Nong Bua Lam Phu', 'Nong Khai', 'Nonthaburi', 'Pathum Thani', 'Pattani', 'Phangnga', 'Phatthalung', 'Phayao', 'Phetchabun', 'Phetchaburi', 'Phichit', 'Phitsanulok', 'Phra Nakhon Si Ayutthaya', 'Phrae', 'Phuket', 'Prachin Buri', 'Prachuap Khiri Khan', 'Ranong', 'Ratchaburi', 'Rayong', 'Roi Et', 'Sa Kaeo', 'Sakon Nakhon', 'Samut Prakan', 'Samut Sakhon', 'Samut Songkhram', 'Saraburi', 'Satun', 'Si Sa Ket', 'Sing Buri', 'Songkhla', 'Sukhothai', 'Suphan Buri', 'Surat Thani', 'Surin', 'Tak', 'Trang', 'Trat', 'Ubon Ratchathani', 'Udon Thani', 'Uthai Thani', 'Uttaradit', 'Yala', 'Yasothon'])

    const {BaseLayer} = LayersControl;
    return (
        <MapContainer center={center} zoom={zoom} scrollWheelZoom={true} style={{height:'100vh'}}>
            <selectProvince/>
            <LayersControl>
                <BaseLayer checked name="Satellite View">
                    <TileLayer
                        url='https://{s}.google.com/vt/lyrs=s&x={x}&y={y}&z={z}'
                        maxZoom= {20}
                        subdomains={['mt1','mt2','mt3']}
                    />
                </BaseLayer>

                <BaseLayer name="OpenStreetMap.Mapik">
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    />
                </BaseLayer>

                <BaseLayer name="maptiler">
                    <TileLayer
                        url='https://api.maptiler.com/maps/hybrid/{z}/{x}/{y}.jpg?key=UWwVBYXDjNUkBGiF7hvU'
                        attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
                        maxZoom= {20}
                    /> 
                </BaseLayer>
            </LayersControl>

            {/* <FeatureGroup>

                {this.state.data.map(data => {

                var myStyleGrid = {
                    color: "white",
                    weight: 0,
                    opacity: 0,
                    fillColor: 'white',
                }
                
                if(data.properties.index < 600){
                    myStyleGrid.fillColor = '#FFEDA0'
                }else if(data.properties.index < 700){
                    myStyleGrid.fillColor = '#FED976'
                }else if(data.properties.index < 800){
                    myStyleGrid.fillColor = '#FEB24C'
                }else if(data.properties.index < 900){
                    myStyleGrid.fillColor = '#FD8D3C'
                }else if(data.properties.index < 1000){
                    myStyleGrid.fillColor = '#FC4E2A'
                }else if(data.properties.index < 1100){
                    myStyleGrid.fillColor = '#E31A1C'
                }else if(data.properties.index < 1200){
                    myStyleGrid.fillColor = '#BD0026'
                }else if(data.properties.index < 1300){
                    myStyleGrid.fillColor = '#800026'
                }
                
                return (
                <GeoJSON key={data.properties.grid_id}  data={data} style={myStyleGrid}>
                    {console.log(data)}
                    <Popup> {data.properties.index} </Popup>
                </GeoJSON>)
                })}
            
            </FeatureGroup> */}
            <Legend/>
        </MapContainer>
  )
}

export default MainMap
